import React, {Component} from 'react';
import Button from './Button.js'
import '../css/ItemView.css';

function ItemView(props){

    const remove = e => {
      let email = props.email
      let itemName = props.ItemName
      e.preventDefault();
    fetch('http://localhost:5000/api/posts/remove', {
    method: 'delete',
            headers: {
          "Content-type": "application/json"
        },
        body: JSON.stringify({
          email,
          itemName
        })
    }
    .then(res => res.json())
    .catch(error => alert(error.message))
    )}

    return (
      <section className="ItemPost">
          <div>
            <i class="far fa-times-circle"></i>
          </div>
          <img id="ItemImage" src={props.imageURL} alt="" />
          <section id="ItemTitle">
            <p>{props.itemName}</p>
            <span>{`$ ${props.itemPrice}`}</span>
          </section>
          <Button onClick={remove} className="profileButton" text={'Delete'}/>

      </section>
    );
  }


export default ItemView;
